import React from 'react';
import { useState } from 'react';



function NewReviewForm({reviews, review, setLocations, setReviews, locationPath, characters}){
    const [open, setOpen] = useState(false);

  const handleOpen = () => {
    setOpen(!open);
  };
    const [newReview, setNewReview] = useState({
        rating: 0,
        text: '',
        location_id: locationPath
    })

    const handleChange = (e) => {
        // const {name, value} = event.target
        setNewReview({...newReview, [e.target.name]:e.target.value})
    }

    const handleSubmit=event => {
        event.preventDefault()


    fetch("http://localhost:9292/reviews", {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json'
        },
        body: JSON.stringify(newReview)
    })
        .then(res => {
            if (res.status === 201){
            res.json()
            .then(current => {
                setReviews(current => {
                const finalEdit = current.findIndex(element => element.id === review.id)
                return [...current.slice(0, finalEdit), reviews.text, ...current.slice(finalEdit + 1)]
            })
            })
        } else {
            res.json()
            .then(error =>{
                alert(error)
            })
        }

        })

}


    return (
      <div>
        <form onSubmit={handleSubmit}>
          <input
            type="number"
            name="rating"
            onChange={handleChange}
            value={newReview.rating}
            placeholder="Rating"
          />
          <input
            type="text"
            name="text"
            onChange={handleChange}
            value={newReview.text}
            placeholder="Text"
          />
          <div className="dropdown">
            <button onClick={handleOpen}>Dropdown</button>
            {open ? (
              <ul className="menu">
                <li className="menu-item">
                  <button>Menu 1</button>
                </li>
                <li className="menu-item">
                  <button>Menu 2</button>
                </li>
                <li className="menu-item">
                  <button>Menu 1</button>
                </li>
                <li className="menu-item">
                  <button>Menu 2</button>
                </li>
              </ul>
            ) : null}
          </div>
          <input type="submit" value="Add" />
        </form>
      </div>
    );
}

export default NewReviewForm;
