import React, {useState, useEffect} from 'react';
// import { NavLink } from 'react-router-dom';
import Review from './Review';
import { Card, Image, Icon } from 'semantic-ui-react';
import { Link, useParams } from 'react-router-dom';

function Location({id, inhabitants, terrain, name, reviews, img_url, average_rating}){
    const {locationId} = useParams(locationId)
    const [location, setLocation] = useState([])

    const mappedReviews = reviews?.map(review => <Review key={review.id} {...review}/>)

    useEffect(() => {
        if (!id) {

            fetch(`http://localhost:9292/locations/${locationId}`)
            .then(resp => resp.json())
            .then(setLocation)
        }
      }, [])
// debugger

    return (
        <Card className='locationCard'
    // href= {`/locations/${id}`}
    header={<Link to = {`/locations/${id}`}>{name}</Link>}
    meta={inhabitants}
    description={terrain}
  />
    );
}

export default Location;
