import React from 'react';
import Review from './Review';
import NewReviewForm from './NewReviewForm';

function DetailContainer({reviews, characters, setReviews}) {
    console.log(window.location.href)
    const locationPath = window.location.href.slice(32)
    const filteredReviews = reviews.filter(review => review.location_id == locationPath)
    const mappedReviews = filteredReviews.map(review => <Review setReviews={setReviews} review={review} character={review.character} key={review.id} />)
    return (
        <div>
            {mappedReviews}
            <NewReviewForm review={review}/>

        </div>
    );
}

export default DetailContainer;
